/**
 * Write a program that takes a list of numbers as input and returns the smallest positive integer that cannot be represented as the sum of any subset of the list.
 * @param {[]} arr 
 * @returns smallest number not generated by any numbebr in array
 */
function findSmallestPositiveInteger(nums) {
    nums.sort((a, b) => a - b);
    let smallestInt = 1;
    for (let i = 0; i < nums.length; i++) {
        if (nums[i] <= smallestInt) {
            smallestInt += nums[i];
            console.log(smallestInt)
        } else {
            break;
        }
    }

    return smallestInt;
}
module.exports = findSmallestPositiveInteger;
